

    var n = new animateControl("main_ani_");
    $(function() {
        function i() {
            wx.config({
                debug: !1,
                appId: "",
                timestamp: 1,
                nonceStr: "",
                signature: "",
                jsApiList: []
            }),
            wx.ready(function() {
                $("#audio").get(0).play()
            })
        }

        var a, s = ($(window).height(), $(window).width(), 0),
        r = 1,
        c = setInterval(function() {
            s >= 360 ? s = 0 : s += 3,
            $(".audio_o").attr("style", "-webkit-transform: rotate(" + s + "deg)")
        },
        50);
        $(".audio").on("click",
        function() {
            r ? (clearInterval(c), $("#audio").get(0).pause(), r = 0) : (c = setInterval(function() {
                s >= 360 ? s = 0 : s += 3,
                $(".audio_o").attr("style", "-webkit-transform: rotate(" + s + "deg)")
            },
            50), $("#audio").get(0).play(), r = 1)
        }),
        i();

        setTimeout(function(){

                $(".loading").fadeOut();
                $(".swiper-container").fadeIn();

                var l = new Swiper($(".swiper-container-main"), {
                    direction: "vertical",
                    speed: 500,
                    grabCursor: !0,
                    autoplay: !1,
                    loop: 1,
                    noSwiping: 0,
                    initialSlide: 0,
                    onInit: function(t) {
                        n.init(swiperAnimateParam_main, t),
                        n.play()
                    },
                    onSlideChangeEnd: function(t) {
                        if (n.play(), 15 == t.activeIndex) {
                            var i = 0;
                            a = setInterval(function() {
                                i++,
                                $(".H_10_1").find("img").eq(i).addClass("active").siblings("img").removeClass("active"),
                                i > 125 && (clearInterval(a), setTimeout(function() {
                                    $(".H_10_1").velocity("transition.fadeOut"),
                                    l.slideTo(16)
                                },
                                2e3), i = 0)
                            },
                            40)
                        }
                    }
                });


        },2000)
    })


    